function [fitresult, gof] = createFit(t1, phi1)
%CREATEFIT(T1,PHI1)
%  Create a fit.
%
%  Data for 'custom fit' fit:
%      X Input : t1
%      Y Output: phi1
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 06-Apr-2017 01:33:41


%% Fit: 'custom fit'.
[xData, yData] = prepareCurveData( t1, phi1 );

% Set up fittype and options.
ft = fittype( '43.54*exp(-zeta*omega*x)*sin(omega*sqrt(1-zeta*zeta)*x + ph)', 'independent', 'x', 'dependent', 'y' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.Lower = [9 0 0];
opts.MaxIter = 10000;
opts.Robust = 'Bisquare';
opts.StartPoint = [9 0 0.0067];
opts.Upper = [15 6.3 1];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
figure( 'Name', 'custom fit' );
h = plot( fitresult, xData, yData );
legend( h, 'phi1 vs. t1', 'custom fit', 'Location', 'NorthEast' );
% Label axes
xlabel t1
ylabel phi1
grid on


